import { Component, Inject } from '@angular/core';
import { HttpClient, HttpParams } from '@angular/common/http';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {
  public weathers: Weather[] = [];

  public pageNum: number = 1;

  public chosenYear: number = 2010;
  public chosenMonth: number = 1;

  getData()
  {
    let param = new HttpParams();
    param.append("Year", (this.chosenYear));
    param.append("Month", (this.chosenMonth));
    param.append("PageNum", this.pageNum);

    //if (this.chosenYear != null)
    //{
    //  if (this.chosenMonth != null)
    //    this.http.get<Weather[]>('https://localhost:7145/GetYearAndMonth', { params: param }).subscribe(result => {
    //      this.weathers = result;
    //    }, error => console.error(error));
    //  else
    //    this.http.get<Weather[]>('https://localhost:7145/GetYear', { params: param }).subscribe(result => {
    //      this.weathers = result;
    //    }, error => console.error(error));
    //}
    //else
    //  this.http.get<Weather[]>('https://localhost:7145/WeatherAPI?PageNum=' + String(this.pageNum)).subscribe(result => {
    //        this.weathers = result;
    //  }, error => console.error(error));

    this.http.get<Weather[]>('https://localhost:7145/GetYearAndMonth', { params: param }).subscribe(result => {
      this.weathers = result;
    }, error => console.error(error));
  }

  constructor(public http: HttpClient)
  {
    this.getData();
  }
}

interface Weather {
  date: string;
  time: string;
  temperature: number;
  humidity: number;
  dewPoint : number;
  presure: number;
  windDirection: string;
  windSpeed: number;
  cloudy: number;
  highBorderCloudy: number;
  horizontalVisibility: number;
}
<h1 id="tableLabel">Архив погоды в Москве</h1>

<p *ngIf="!weathers"><em>Loading...</em></p>

<table class='table table-striped' aria-labelledby="tableLabel" *ngIf="weathers">
  <thead>
    <tr>
      <th>Дата</th>
      <th>Время</th>
      <th>Темп.</th>
      <th>Влаж.</th>
      <th>Точка росы</th>
      <th>Давление</th>
      <th>Напр. ветра</th>
      <th>Скорость ветра</th>
      <th>Облачность</th>
      <th>Граница облачности</th>
      <th>Горизонт. видимость</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let weather of weathers">
      <td>{{ weather.date }}</td>
      <td>{{ weather.time }}</td>
      <td>{{ weather.temperature }}</td>
      <td>{{ weather.humidity }}</td>
      <td>{{ weather.dewPoint }}</td>
      <td>{{ weather.presure }}</td>
      <td>{{ weather.windDirection }}</td>
      <td>{{ weather.windSpeed }}</td>
      <td>{{ weather.cloudy }}</td>
      <td>{{ weather.highBorderCloudy }}</td>
      <td>{{ weather.horizontalVisibility }}</td>
    </tr>
  </tbody>
</table>

<form action="">
  <div>
    <label for="chosenYear">Введите год</label>
    <input type="text" required name="chosenYear" [(ngModel)]="chosenYear" />
    <label for="chosenYear">Введите месяц</label>
    <input type="text" required name="chosenYear" [(ngModel)]="chosenMonth" />
    <label for="pageName">Номер страницы</label>
    <input type="text" required name="pageNum" [(ngModel)]="pageNum" />
    <button (click)="getData()">Перейти к странице</button>
  </div>
</form>
