import { HttpClient } from '@angular/common/http';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Component, OnInit } from '@angular/core';
import { UploadFilesService } from 'src/app/services/upload-files.service';
import { HttpEventType, HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-upload-data',
  templateUrl: './upload-data.component.html',
  styleUrls: ['./upload-data.component.css']
})
export class UploadDataComponent
{
  message = '';

  constructor(private uploadService: UploadFilesService, public selectedFiles: FileList, public fileInfos: Observable<any>) { }

  selectFiles(event) {
    this.selectedFiles = event.target.files;
  }

  uploadFiles() {
    this.message = '';

    for (let i = 0; i < this.selectedFiles.length; i++) {
      this.upload(i, this.selectedFiles[i]);
    }
  }

  upload(idx, file) {
    this.uploadService.upload(file).subscribe(
      event => {
        if (event.type === HttpEventType.UploadProgress) {
        } else if (event instanceof HttpResponse) {
        }
      },
      err => {
        this.message = 'Could not upload the file:' + file.name;
      });
  }

  ngOnInit() {
  }
}
<h1>Загрузка Excel файлов</h1>

<form action="" method="post" enctype="multipart/form-data">
  <p>Загрузите ваши таблицы на сервер</p>
  <p>
    <input type="file" name="excelFiles" multiple (change)="selectFiles($event)">
    <button (click)="uploadFiles" [disabled]="!selectedFiles">Отправить</button>
  </p>
</form>
