import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-upload-data',
  templateUrl: './upload-data.component.html',
  styleUrls: ['./upload-data.component.css']
})
export class UploadDataComponent {

  public formFiles: File[] = [];

  sendFiles()
  {
    const formData = new FormData();
    console.log(this.formFiles.length);
    for (const file of this.formFiles)
      formData.append('FormFile', file);

    this.http.post("https://localhost:7145/WeatherApi", formData).subscribe();
    //const request = new XMLHttpRequest();
    //request.open("POST", "https://localhost:7145/WeatherApi", false);



    //request.send(formData);
  }
  constructor(public http: HttpClient) {  }
}
<h1>Загрузка Excel файлов</h1>

<form [formGroup]="uploadForm" (ngSubmit)="sendFiles">
  <p>Загрузите ваши таблицы на сервер</p>
  <p>
    <input type="file" name="excelTable" multiple [(ngModel)]="formFiles">
    <button (click)="sendFiles()">Отправить</button>
  </p>
</form>
